mapper.select:
select productNum, productName, price, purchaseAmount, productImage
from member m, cart c, product p
where m.userID = c.userID and c.productNum=p.productNum and c.userID=#{userID, jdbcType=VARCHAR}

select nvl(sum(price*cartAmount), 0) money
from cart c, product p
where c.productNum=p.productNum and userID=#{userID}
